import React, { useEffect, useState } from 'react';
import Song from './Song';
import Podcast from './Podcast';
import './App.css';
import shuffleButton from '/Users/matt/info655-assignment2/src/shuffleButton.png';
import backButton from '/Users/matt/info655-assignment2/src/backButton.png';
import playButton from '/Users/matt/info655-assignment2/src/playButton.png';
import forwardButton from '/Users/matt/info655-assignment2/src/nextButton.png';

const Playlist = () => {

  const [playlist, setPlaylist] = useState([]);

  const getSongs = () => {
    const xhr = new XMLHttpRequest();
    xhr.open("GET", "http://localhost:3001/tracks");
    xhr.send();
    xhr.responseType = "json";
    xhr.onload = () => {
      if (xhr.readyState == 4 && xhr.status == 200) {
        const data = xhr.response;
        setPlaylist(data);
      } else {
        console.log(`Error: ${xhr.status}`);
      }
    };
  };

  useEffect (getSongs, []);

  const shufflePlaylist = () => {
    let shuffledPlaylist = [...playlist];
    shuffledPlaylist.sort(() => Math.random() - 0.5);
  setPlaylist(shuffledPlaylist);
  };

  let nowPlayingIndex = 0;

const nowPlaying = () => {
  return playlist[nowPlayingIndex];
}

const nextButton = () => {
  nowPlayingIndex = (nowPlayingIndex + 1) % playlist.length;
  return nowPlaying();
}

const previousButton = () => {
  nowPlayingIndex = (nowPlayingIndex - 1 + playlist.length) % playlist.length;
  return nowPlaying();
}

  return (
    <div>
      <div className="toolbar">
        <button onClick={shufflePlaylist}>
          <img className="shuffle" src={shuffleButton} />
        </button>

        <button onClick={backButton}>
          <img className="previous" src={backButton} />
        </button>

        <button>
          <img className="play" src={playButton} />
        </button>

        <button onClick={forwardButton}>
          <img className="next" src={forwardButton} />
        </button>
      </div>

      {playlist.map((item, index) => {
        if ('artist' in item) {
          return <Song key={index} {...item} />;
        } else {
          return <Podcast key={index} {...item} />;
        }
      })}
    </div>
  );
};

export default Playlist;
