import React, { useEffect, useState } from 'react';
import Song from './Song';
import Podcast from './Podcast';
import './App.css';
import shuffleButton from '/Users/matt/info655-assignment2/src/shuffleButton.png';
import backButton from '/Users/matt/info655-assignment2/src/backButton.png';
import playButton from '/Users/matt/info655-assignment2/src/playButton.png';
import forwardButton from '/Users/matt/info655-assignment2/src/nextButton.png';

const Playlist = () => {

  const [playlist, setPlaylist] = useState([]);

  const getSongs = () => {
    const xhr = new XMLHttpRequest();
    xhr.open("GET", "http://localhost:3001/tracks");
    xhr.send();
    xhr.responseType = "json";
    xhr.onload = () => {
      if (xhr.readyState == 4 && xhr.status == 200) {
        const data = xhr.response;
        setPlaylist(data);
      } else {
        console.log(`Error: ${xhr.status}`);
      }
    };
  };

  useEffect (getSongs, []);

  const shufflePlaylist = () => {
    let shuffledPlaylist = [...playlist];
    shuffledPlaylist.sort(() => Math.random() - 0.5);
  setPlaylist(shuffledPlaylist);
  };

const [nowPlaying, setNowPlaying] = useState('');
const [nowPlayingIndex, setNowPlayingIndex] = useState(0);

const handleDoubleClick = (title, episodeTitle, index) => {
  // Also update nowPlayingIndex here
  setNowPlayingIndex(index);
  if (title) {
    setNowPlaying(`Now Playing: ${title}`);
  } else if (episodeTitle) {
    setNowPlaying(`Now Playing: ${episodeTitle}`);
  }
};

const nextSong = () => {
  if (nowPlayingIndex < playlist.length - 1) {
    // Use the setter function here
    setNowPlayingIndex(nowPlayingIndex + 1);
    setNowPlaying(`Now Playing: ${playlist[nowPlayingIndex + 1].title}`);
  }
};

const backSong = () => {
  if (nowPlayingIndex > 0) {
    // And here
    setNowPlayingIndex(nowPlayingIndex - 1);
    setNowPlaying(`Now Playing: ${playlist[nowPlayingIndex - 1].title}`);
  }
};

   return (
      <div>
        <div className="toolbar">
          <button onClick={shufflePlaylist}>
            <img className="shuffle" src={shuffleButton} />
          </button>

          <button onClick={backSong}>
            <img className="previous" src={backButton} />
          </button>

          <button>
            <img className="play" src={playButton} />
          </button>

          <button onClick={nextSong}>
            <img className="next" src={forwardButton} />
          </button>
        </div>

        <div className="status">
          {nowPlaying}
        </div>

    {playlist.map((item, index) => {
      if ('artist' in item) {
    return <Song key={index} {...item} onDoubleClick={() => handleDoubleClick(item.title, null, index)} />;
      } else {
    return <Podcast key={index} {...item} onDoubleClick={() => handleDoubleClick(null, item.episodeTitle, index)} />;
      }
    })}

      </div>
    );
  };

export default Playlist;
