import Song from './Song';
import Podcast from './Podcast';
import './App.css';

const Playlist = () => {
  const [playlist, setPlaylist] = useState([]);

  const songs = [
    { title: 'A World So Full of Love', artist: 'Shakey Graves', year: 2019 },
    { title: 'The 30th', artist: 'Billie Eilish', year: 2022 },
    { title: 'August', artist: 'Flipturn', year: 2020 },
  ];

  const podcasts = [
    { episode: 1, episodeTitle: 'Stuff You Should Know: Boba Tea' },
    { season: 4, episode: 2, episodeTitle: 'The Life And Works Of J.D. Salinger' },
    { season: 3, episode: 14, episodeTitle: 'The Matewan Massacre' },
  ];

  useEffect(() => {
    const shuffledPlaylist = [...songs, ...podcasts].sort(() => Math.random() - 0.5);
    setPlaylist(shuffledPlaylist);
  }, []);

  return (
    <div>
      <h2 className="playlistTitle2">Sad Rainy Day:</h2>
      {playlist.map((item, index) => {
        if ('artist' in item) {
          return <Song key={index} {...item} />;
        } else {
          return <Podcast key={index} {...item} />;
        }
      })}
    </div>
  );
};

export default Playlist;
