import React, { useEffect, useState } from 'react';
import Song from './Song';
import Podcast from './Podcast';
import './App.css';
import shuffleButton from '/Users/matt/info655-assignment2/src/shuffleButton.png';
import previousButton from '/Users/matt/info655-assignment2/src/backButton.png';
import playButton from '/Users/matt/info655-assignment2/src/playButton.png';
import nextButton from '/Users/matt/info655-assignment2/src/nextButton.png';

const Playlist = () => {

  const getSongs = () => {
    const xhr = new XMLHttpRequest();
    xhr.open("GET", "http://localhost:3001/0");
    xhr.send();
    xhr.responseType = "json";
    xhr.onload = () => {
      if (xhr.readyState == 4 && xhr.status == 200) {
        const data = xhr.response;
        console.log(data);
      } else {
        console.log(`Error: ${xhr.status}`);
      }
    };
  };

  useEffect (getSongs, []);

  const songs = [
    { title: 'A World So Full of Love', artist: 'Shakey Graves', year: 2019 },
    { title: 'The 30th', artist: 'Billie Eilish', year: 2022 },
    { title: 'August', artist: 'Flipturn', year: 2020 },
  ];

  const podcasts = [
    { episode: 186, episodeTitle: 'Stuff You Should Know: Boba Tea' },
    { season: 4, episode: 22, episodeTitle: 'The Life And Works Of J.D. Salinger' },
    { season: 3, episode: 14, episodeTitle: 'The Matewan Massacre' },
  ];

  const [playlist, setPlaylist] = useState([
    ...songs, ...podcasts
  ]);

  const shufflePlaylist = () => {
    let shuffledPlaylist = [...playlist];
    shuffledPlaylist.sort(() => Math.random() - 0.5);
  setPlaylist(shuffledPlaylist);
  };

  const previousSong = () => {
  }

  const nextSong = () => {
  }

  return (
    <div>
      <h2 className="playlistTitle2">Sad Rainy Day:</h2>

      <button onClick={shufflePlaylist}>
        <img className="shuffle" src={shuffleButton} />
      </button>

      <button onClick={previousSong}>
        <img className="previous" src={previousButton} />
      </button>

      <button onClick={nextSong}>
        <img className="next" src={nextButton} />
      </button>

      {playlist.map((item, index) => {
        if ('artist' in item) {
          return <Song key={index} {...item} />;
        } else {
          return <Podcast key={index} {...item} />;
        }
      })}
    </div>
  );
};

export default Playlist;
