        </button>

        <button onClick={previousSong}>
          <img className="previous" src={previousButton} />
        </button>

        <button>
          <img className="pause" src={playButton} />
        </button>

        <button onClick={nextSong}>
          <img className="next" src={nextButton} />
        </button>
      </div>

      {playlist.map((item, index) => {
        if ('artist' in item) {
          return <Song key={index} {...item} />;
        } else {
          return <Podcast key={index} {...item} />;
        }
      })}
    </div>
  );
};

export default Playlist;
